targets.expr:
100 * (
  1 
  - 
  (
    avg(
      avg_over_time(
        node_memory_MemFree_bytes{instance=~"$node"}[$__rate_interval]
      ) + 
      avg_over_time(
        node_memory_Cached_bytes{instance=~"$node"}[$__rate_interval]
      ) + 
      avg_over_time(
        node_memory_Buffers_bytes{instance=~"$node"}[$__rate_interval]
      )
    ) by (instance)
  )
  / 
  avg(node_memory_MemTotal_bytes{instance=~"$node"}
  ) by (instance)
)
